section .text
_start:
    COMPARE:
        inc CODE_OFFSET
        cmp [CODE + CODE_OFFSET], '+'
        jz PLUS
        cmp [CODE + CODE_OFFSET], '-'
        jz MINUS
        cmp [CODE + CODE_OFFSET], '>'
        jz NEXT
        cmp [CODE + CODE_OFFSET], '<'
        jz BACK
        cmp [CODE + CODE_OFFSET], '.'
        jz PRINT
        cmp [CODE + CODE_OFFSET], ','
        jz INPUT
        jmp UNKNOWN
    PLUS:
        inc [CELLS + CELL_OFFSET]
        jmp COMPARE
    MINUS:
        dec [CELLS + CELL_OFFSET]
        jmp COMPARE
    NEXT:
        inc CODE_OFFSET
        jmp COMPARE
    BACK:
        dec CODE_OFFSET
        jmp COMPARE
    PRINT:
        mov rax, 1
        mov rdi, 1
        mov rsi, [CELLS + CELL_OFFSET]
        mov rdx, 1
        syscall
        jmp COMPARE
    INPUT:
        mov rax, 0
        mov rdi, 0
        mov rsi, [CELLS + CELL_OFFSET]
        mov rdx, 1
        syscall
        jmp COMPARE
    START_CYCLE:
        push CODE_OFFSET
        jmp COMPARE
    END_CYCLE:
        cmp [CELLS + CELL_OFFSET], 0
        jz IS_ZERO
        jnz ISNT_ZERO
        IS_ZERO:
            pop rbx
            jmp COMPARE
        ISNT_ZERO:
            pop rbx
            push rbx
            mov CODE_OFFSET, rbx
            jmp COMPARE
    UNKNOWN:
        mov rax, 60
        mov rdi, 1
        syscall